sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   //if (isLoading || newValue === '') {
   //   return;
  // }

	var caller = g_form.getReference('u_requested_for', doAlert); // doAlert is our callback function } 
		function doAlert(caller) { //reference is passed into callback as first arguments
			//alert("caller user_name: " + caller.user_name);
			var ga = new GlideAjax('GetUserInfo');
			ga.addParam('sysparm_name','managerName');
			ga.addParam('sysparm_user_name', caller.user_name);
			ga.getXMLAnswer(ManagerParse);
		}

			function ManagerParse(response) {
				//alert(response);
				var answer = JSON.parse(response);
				var department = answer.department;
				var departmentID = "Department ID: " + answer.id;
				alert(departmentID);
				switch (String(department)) {
					case "IT":
						g_form.addDecoration('u_requested_for', 'icon-database', departmentID, 'color-red');
						break;
					case "HR":
						g_form.addDecoration('u_requested_for', 'icon-book', departmentID, 'color-green');
						break;
					case "Finance":
						g_form.addDecoration('u_requested_for', 'icon-star', departmentID, 'color-yellow');
						break;
					case "":
						g_form.addDecoration('u_requested_for', 'icon-help', 'No Department Assigned', 'color-blue');
						break;
					default:
						g_form.addDecoration('u_requested_for', 'icon-help', departmentID, 'color-blue');
					}
			} 
}
