sys_script_include.script:
var GetUserInfo = Class.create();
GetUserInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	managerName: function() {
		var userName = this.getParameter("sysparm_user_name");
		var grUser = new GlideRecord('sys_user');
		grUser.get("user_name", userName);
		var department = grUser.getDisplayValue('department');
		var grDepartment = new GlideRecord('cmn_department');
		grDepartment.get("name", department);
		var departmentID = grDepartment.getDisplayValue('id');	
		var result = {
			"department": department,
			"id": departmentID
		};
		return JSON.stringify(result);	
	},
    type: 'GetUserInfo'
});
